import React, { useState } from 'react'
import BigAnimeCard from './BigAnimeCard'

export default function ListBigAnimeCard() {
  const uri = ["https://cdn.animevietsub.biz/data/big_banner/2024/07/25/animevsub-JuFHCfaylF.jpg",
    "https://cdn.animevietsub.biz/data/big_banner/2024/07/28/animevsub-RguWj1xzsD.jpg",
    "https://cdn.animevietsub.biz/data/big_banner/2024/11/30/animevsub-nULCnae9rZ.png",
    "https://cdn.animevietsub.biz/data/big_banner/2024/10/13/animevsub-jEV4aprprp.jpg",
  ]

  const [position, setPosition] = useState(0)
  const [dragging, setDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const[index,setIndex]=useState(0)

  const handleMouseDown = (e) => {
    if(index===uri.length-1)
      return
    setDragging(true)
    setPosition(e.clientx)
    setIndex(index+1)
  }

  const handleMouseMove = (e) => {
    if (!dragging) return
    const deltax = e.clientx - startX
    setPosition((prev) => prev + deltax)
    setStartX(e.clientx)
  }

  const handleMouseUp = () => {
    setDragging(false)
  }
  return (
    <div className='w-2/3 flex overflow-hidden '
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}>
      {uri.map((item) => (
        <div key={index} className='flex-shrink-0 w-full'
          style={{
            transform: `translateX(-${index*100}%)`,
          }}
          onMouseDown={handleMouseDown}>
          <BigAnimeCard imgSrc={item} />
        </div>
      ))}
    </div>
  )
}
