import React, { useState } from 'react';

export default function ListAnime() {
    const uri = ["https://cdn.animevietsub.biz/data/poster/2024/11/30/animevsub-ptJf6ZLFGE.jpg","https://cdn.animevietsub.biz/data/poster/2024/10/14/animevsub-LosRIhuZV2.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-TIHly52n6l.jpg","https://cdn.animevietsub.biz/data/poster/2024/11/03/animevsub-DYEUQETfFh.jpg"
        ,"https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-Amu2bMVysF.jpg","https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg"
    ];
    let imgs = [];
    for (let i = 0; i < 6; i++) {
        imgs.push(uri[i]);
    }
    const [start, setStart] = useState(0);

    const show = imgs.slice(start, start + 4);  // Hiển thị 8 ảnh từ vị trí start

    const toleft = () => {
        if (start > 0) {
            setStart(start - 1);  // Di chuyển sang trái (hiển thị ảnh cũ hơn)
        }
    };

    const toright = () => {
        if (start + 4 < imgs.length) {
            setStart(start + 1); 
        }
        else
            setStart(0)
    };

    return (
        <div className='flex gap-2 overflow-x-hidden scroll-smooth scrollbar-hide justify-center relative'>
            <button className='bg-red-700 bg-opacity-90 self-center h-fit absolute left-0 z-10' onClick={toleft}>
                &#8592;
            </button>
            <div className='flex transition-transform duration-500 ease-in-out gap-2 animate-fade-left animate-'>
                {show.map((img, index) => (
                    <img key={index} src={img} alt={index} className='w-[120px]' />
                ))}
            </div>
            <button className='bg-red-700 bg-opacity-90 self-center h-fit absolute right-0 z-10' onClick={toright}>
                &#8594;
            </button>
        </div>
    );
}
