import React, { useEffect, useRef } from 'react';
import Hls from 'hls.js';

const VideoPlayer = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    const videoUrl = "https://cdn.animevui.com/file/a288dc863e72c35ede338b1011b78168"; // URL video HLS
    const hls = new Hls();
    if (Hls.isSupported()) {
      hls.loadSource(videoUrl); // Tải nguồn video HLS
      hls.attachMedia(videoRef.current); // Gắn video vào thẻ video
      hls.on(Hls.Events.MANIFEST_PARSED, function () {
        videoRef.current.play(); // Phát video khi manifest được phân tích
      });
    } else if (videoRef.current.canPlayType('application/vnd.apple.mpegurl')) {
      // Nếu trình duyệt hỗ trợ HLS natively (Safari)
      videoRef.current.src = videoUrl;
      videoRef.current.addEventListener('loadedmetadata', function () {
        videoRef.current.play();
      });
    }

    return () => {
      if (Hls.isSupported()) {
        hls.destroy(); // Giải phóng tài nguyên khi component bị hủy
      }
    };
  }, []);

  return (
    <div>
      <video ref={videoRef} width="100%" height="auto" controls>
        Your browser does not support the video tag.
      </video>
    </div>
  );
};

export default VideoPlayer;
