import React, { useState } from 'react';

export default function ListAnime() {
    const uri = "https://cdn.animevietsub.biz/data/poster/2024/11/30/animevsub-ptJf6ZLFGE.jpg";
    let imgs = [];
    for (let i = 0; i < 12; i++) {
        imgs.push(uri);
    }
    const [displayImgs, setDisplayImgs] = useState(imgs.slice(0, 8)); // Hiển thị 8 ảnh ban đầu

    const toleft = () => {
        const updatedImgs = [...displayImgs];  // Sao chép mảng hiện tại
        updatedImgs.push(updatedImgs.shift()); // Di chuyển ảnh đầu tiên sang cuối mảng
        setDisplayImgs(updatedImgs);  // Cập nhật mảng ảnh mới
    };

    const toright = () => {
        const updatedImgs = [...displayImgs];  // Sao chép mảng hiện tại
        updatedImgs.unshift(updatedImgs.pop()); // Di chuyển ảnh cuối cùng ra đầu mảng
        setDisplayImgs(updatedImgs);  // Cập nhật mảng ảnh mới
    };

    return (
        <div className='flex gap-2 overflow-x-hidden scroll-smooth scrollbar-hide justify-center relative'>
            <button className='bg-red-700 bg-opacity-90 self-center h-fit absolute left-0 z-10' onClick={toleft}>
                &#8592;
