import React, { useState } from 'react';

export default function ListAnime() {
    const uri = "https://cdn.animevietsub.biz/data/poster/2024/11/30/animevsub-ptJf6ZLFGE.jpg";
    let imgs = [];
    for (let i = 0; i < 10; i++) {
        imgs.push(uri);
    }
    const [start, setStart] = useState(0);

    const show = imgs.slice(start, start + 8);  // Hiển thị 8 ảnh từ vị trí start

    const toleft = () => {
        if (start > 0) {
            setStart(start - 1);  // Di chuyển sang trái (hiển thị ảnh cũ hơn)
        }
    };

    const toright = () => {
        if (start + 8 < imgs.length) {
            setStart(start + 1);  // Di chuyển sang phải (hiển thị ảnh mới hơn)
        }
    };

    return (
        <div className='w-[60vw] flex gap-2 overflow-x-hidden scroll-smooth scrollbar-hide justify-center relative'>
            <button className='bg-red-700 bg-opacity-90 self-center h-fit absolute left-0 z-10' onClick={toleft}>
                &#8592;
            </button>
            <div className='flex transition-transform duration-500 ease-in-out gap-2' style={{ transform: `translateX(-${start * 120}px)` }}>
                {show.map((img, index) => (
                    <img key={index} src={img} alt='img' className='w-[120px]' />
                ))}
            </div>
            <button className='bg-red-700 bg-opacity-90 self-center h-fit absolute right-0 z-10' onClick={toright}>
                &#8594;
            </button>
        </div>
    );
}
