import React, { useEffect, useState } from 'react'
import { getAnime } from '../Service/Anime'
import { useParams } from 'react-router-dom'

export default function DetailofAnime() {
  const {id}=useParams()
  const [Anime, setAnime] = useState()
  useEffect(() => {
    getAnime(id).then((data)=>{
      setAnime(data);
    })
  },[])

  if (!Anime) {
    return
  }

  return (
    <div className='w-full h-[350px] relative'>
      <img src={Anime.BigImg} alt="img" className='h-full w-full object-cover object-top rounded' />
      <div className='absolute inset-0 w-full h-full bg-black bg-opacity-40'>
          <div className='absolute w-[30%] p-5 bottom-4'>
            <img src={Anime.Img} alt="img" />
            <button className='absolute bottom-0'>XEM PHIM</button>
          </div>
      </div>
    </div>
  )
}
