import React, { useEffect, useState } from 'react'
import { getAnime } from '../Service/Anime'
import { useParams } from 'react-router-dom'

export default function DetailofAnime() {
  const { id } = useParams()
  const [Anime, setAnime] = useState()
  useEffect(() => {
    getAnime(id).then((data) => {
      setAnime(data);
    })
  }, [id])

  if (!Anime) {
    return
  }

  return (
    <div className='w-full h-[350px] relative'>
      <img src={Anime.BigImg} alt="img" className='absolute h-full w-full object-cover object-top rounded' />
      <div className='absolute w-full h-full bg-black bg-opacity-60'>
        <div className='p-5 w-full absolute top-7 flex justify-between'>
        <div className=' w-[25%]'>
          <div className='relative'>
            <img src={Anime.Img} alt="img" />
            <button className='absolute bottom-5 w-full text-white text-[20px] bg-red-600 bg-opacity-80 p-2'>XEM PHIM</button>
          </div>
        </div>
        <div className=' w-[70%] '>
          <h1 className='text-lime_green text-[30px] font-bold truncate mb-3'>{Anime.Name}</h1>
          <p className='text-white text-opacity-80 font-semibold text-[13px] overflow-y-scroll max-h-[30%] scrollable scroll-smooth'>{Anime.Describe}</p>
          <hr className='mt-3 h-[1px] border-0 bg-white bg-opacity-40'/>
          <div className='flex'>
              <span className='size-10 rounded-full'>
                {Anime.Rating}
              </span>
          </div>
        </div>
        </div>
      </div>
    </div>
  )
}
