import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [HocSinh, setItems] = useState([]);
    const [newItem, setNewItem] = useState({ Name: '', Age:'' ,Address:'',Gender:''});

    useEffect(() => {
        // Gọi API để lấy dữ liệu
        axios.get('http://localhost:5000/items')
            .then(response => setItems(response.data))
            .catch(error => console.error(error));
    }, []);

    const addItem = () => {
        // Gọi API để thêm dữ liệu
        axios.post('http://localhost:5000/items', newItem)
            .then(response => {
                setItems([...items, response.data]);
                setNewItem({ Name: '', Age:'' ,Address:'',Gender:''});
            })
            .catch(error => console.error(error));
    };

    return (
        <div>
            <h1>Items</h1>
            <ul>
                {HocSinh.map(item => (
                    <li key={item._id}>
                        {item.Name} - {item.Age} - {item.Address} - {item.Gender}
                    </li>
                ))}
            </ul>
            <input
                type="text"
                placeholder="Name"
                value={newItem.Name}
                onChange={e => setNewItem({ ...newItem, Name: e.target.value })}
            />
            <input
                type="text"
                placeholder="Age"
                value={newItem.Age}
                onChange={e => setNewItem({ ...newItem, Age: +e.target.value })}
            />
            <input
                type="text"
                placeholder="Address"
                value={newItem.Address}
                onChange={e => setNewItem({ ...newItem, Address: +e.target.value })}
            />
            <input
                type="text"
                placeholder="Gender"
                value={newItem.Gender}
                onChange={e => setNewItem({ ...newItem, Gender: +e.target.value })}
            />
            <button onClick={addItem}>Add Item</button>
        </div>
    );
};

export default App;
