import React, { useEffect, useState } from 'react';
import AnimeCard from './AnimeCard';

export default function ListAnime() {
    const uri = ["https://cdn.animevietsub.biz/data/poster/2024/11/30/animevsub-ptJf6ZLFGE.jpg", 
                 "https://cdn.animevietsub.biz/data/poster/2024/10/14/animevsub-LosRIhuZV2.jpg",
                 "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-TIHly52n6l.jpg", 
                 "https://cdn.animevietsub.biz/data/poster/2024/11/03/animevsub-DYEUQETfFh.jpg",
                 "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-Amu2bMVysF.jpg",
                 "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg"
    ];
    let imgs = [];
    for (let i = 0; i < 6; i++) {
        imgs.push(uri[i]);
    }
    const [start, setStart] = useState(0);
    const [numberImgs,setNumberImgs]=useState(6);

    const updateNumber=()=>{
        if(window.innerWidth<786)
            setNumberImgs(4)
        else
            setNumberImgs(6)
    }

    useEffect(()=>{
        updateNumber();
        window.addEventListener('resize',updateNumber);
        return ()=>{
            window.removeEventListener('resize',updateNumber)
        }
    },[])

    const show = imgs.slice(start, start + numberImgs);  // Hiển thị 8 ảnh từ vị trí start

    const toleft = () => {
        if (start > 0) {
            setStart(start - 1);  // Di chuyển sang trái (hiển thị ảnh cũ hơn)
        }
    };

    const toright = () => {
        if (start + numberImgs < imgs.length) {
            setStart(start + 1);
        }
        else
            setStart(0)
    };

    return (
        <div className='flex overflow-x-hidden scroll-smooth scrollbar-hide relative lg:w-[60vw] md:w-[80vw] sm:w-full '>
            <button className='bg-red-700 bg-opacity-90 self-center h-fit absolute left-0 z-10' onClick={toleft}>
                &#8592;
            </button>
            <div className='flex gap-4'>
                {show.map((img, index) => (
                    <AnimeCard key={index} imgSrc={img}></AnimeCard>
                ))}
            </div>
            <button className='bg-red-700 bg-opacity-90 self-center h-fit absolute right-0 z-10' onClick={toright}>
                &#8594;
            </button>
        </div>
    );
}
