import React, { useState } from 'react'

export default function ListAnime() {
    const uri = "https://cdn.animevietsub.biz/data/poster/2024/11/30/animevsub-ptJf6ZLFGE.jpg"
    let imgs = [];
    for (let i = 0; i < 10; i++) {
        imgs.push(uri);
    }
    const [start, setStart] = useState(0);

    const sliderRef = useRef(null);

    const show = imgs.slice(start, start + 8)

    const toleft = () => {
        if (start > 0) {
            setStart(start - 1);
            sliderRef.current.scrollBy({ left: -200, behavior: "smooth" });
        }

        console.log(start)
    }
    const toright = () => {
        if (start + 8 < imgs.length) {
            setStart(start + 1)
            sliderRef.current.scrollBy({ left: 200, behavior: "smooth" });
        }
        else {
            sliderRef.current.scrollBy({ left: start * 200, behavior: "smooth" });
            setStart(0)
        }
        console.log(start)
    }

    return (
        <div className='w-[60vw] flex gap-2 overflow-x-scroll scroll-smooth'>
            <button className='bg-red-700 bg-opacity-90 self-center h-fit' onClick={toleft}>
                &#8592;
            </button>
            {show.map((img) => (
                <img src={img} alt='img' className='w-[120px]'></img>
            ))}
            <button className='bg-red-700 bg-opacity-90 self-center h-fit' onClick={toright}>
                &#8594;
            </button>
        </div>
    )
}
