import React, { useState } from 'react';
import AnimeCard from './AnimeCard';

export default function ListAnime() {
    const uri = [
        "https://cdn.animevietsub.biz/data/poster/2024/11/30/animevsub-ptJf6ZLFGE.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/10/14/animevsub-LosRIhuZV2.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-TIHly52n6l.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/11/03/animevsub-DYEUQETfFh.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-Amu2bMVysF.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
    ];

    const [start, setStart] = useState(0);

    const toLeft = () => {
        if (start > 0) {
            setStart(start - 1);
        }
    };

    const toRight = () => {
        if (start + 4 < uri.length) {
            setStart(start + 1);
        } else {
            setStart(0); // Quay về đầu nếu hết ảnh
        }
    };

    return (
        <div className="relative w-full lg:w-[60vw] md:w-[80vw] sm:w-full overflow-hidden">
            {/* Nút sang trái */}
            <button
                className="bg-red-700 bg-opacity-90 absolute left-0 top-1/2 -translate-y-1/2 p-2 z-10"
                onClick={toLeft}
            >
                &#8592;
            </button>

            {/* Container hình ảnh */}
            <div
                className="flex transition-transform duration-500"
                style={{
                    transform: `translateX(-${start * (100 / 4)}%)`,
                    width: `${(uri.length * 100) / 4}%`,
                }}
            >
                {uri.map((img, index) => (
                    <div
                        key={index}
                        className="flex-shrink-0"
                        style={{ width: `${100 / 4}%` }}
                    >
                        <AnimeCard imgSrc={img} />
                    </div>
                ))}
            </div>

            {/* Nút sang phải */}
            <button
                className="bg-red-700 bg-opacity-90 absolute right-0 top-1/2 -translate-y-1/2 p-2 z-10"
                onClick={toRight}
            >
                &#8594;
            </button>
        </div>
    );
}
