import React, { useState } from 'react'
import BigAnimeCard from './BigAnimeCard'

export default function ListBigAnimeCard() {
  const uri = ["https://cdn.animevietsub.biz/data/big_banner/2024/07/25/animevsub-JuFHCfaylF.jpg",
    "https://cdn.animevietsub.biz/data/big_banner/2024/07/28/animevsub-RguWj1xzsD.jpg",
    "https://cdn.animevietsub.biz/data/big_banner/2024/11/30/animevsub-nULCnae9rZ.png",
    "https://cdn.animevietsub.biz/data/big_banner/2024/10/13/animevsub-jEV4aprprp.jpg",
  ]

  const [position, setPosition] = useState(0)
  const [dragging, setDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [deltaX, setDeltaX] = useState(0);

  const handleMouseDown = (e) => {
    setDragging(true)
    setStartX(e.clientX)
  }

  const handleMouseMove = (e) => {
    if (!dragging) return;
    const currentDelta = e.clientX - startX;
    setDeltaX(currentDelta); 
  }

  const handleMouseUp = () => {
    setDragging(false);
    setDeltaX(0);
    console.log(position)
    if(deltaX<0 &&position<3)
      setPosition(position+1)
    if(deltaX>0&&position>0)
      setPosition(position-1)
  }
  return (
    <div className='overflow-hidden'>
      <div className='flex transition-transform duration-500 ease-in-out'
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseDown={handleMouseDown}
        style={{
          transform: `translateX(-${position*100}%)`,
        }}>
        {uri.map((item) => (
          <div className='flex-shrink-0 w-full relative'>
            <BigAnimeCard imgSrc={item}/>
            <div className='absolute bottom-0 right-0 flex'>
              {Array.from({length:uri.length},(_,index)=>(
                <div key={index} className='size-2 rounded-full bg-slate-500'></div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
