import React, { useState } from 'react';
import AnimeCard from './AnimeCard';

export default function ListAnime() {
    const uri = [
        "https://cdn.animevietsub.biz/data/poster/2024/11/30/animevsub-ptJf6ZLFGE.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-TIHly52n6l.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/11/03/animevsub-DYEUQETfFh.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-Amu2bMVysF.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/11/03/animevsub-DYEUQETfFh.jpg",
        "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-Amu2bMVysF.jpg",
    ];

    const [start, setStart] = useState(0);
    
    const toLeft = () => {
        if (start > 0) {
            setStart(start - 1); // Di chuyển sang trái
        }
    };

    const toRight = () => {
        if (start + visibleImages < uri.length) {
            setStart(start + 1); // Di chuyển sang phải
        } else {
            setStart(0); // Quay lại đầu
        }
    };

    const gap = 16; // Khoảng cách giữa các ảnh (px)
    const visibleImages = 8; // Số ảnh hiển thị cùng lúc

    return (
        <div className="flex overflow-hidden h-fit relative lg:w-[60vw] md:w-[80vw] sm:w-full">
            {/* Nút sang trái */}
            <button
                className="bg-red-700 bg-opacity-90 self-center size-6 absolute left-0 z-10"
                onClick={toLeft}
            >
                &#8592;
            </button>

            {/* Container ảnh */}
            <div
                className="flex gap-4 transition-transform duration-500 ease-in-out"
                style={{
                    transform: `translateX(calc(-${start * (100 / visibleImages)}% - ${start * (gap/visibleImages)}px))`,
                }}
            >
                {uri.map((img, index) => (
                    <div
                        key={index}
                        className="flex-shrink-0"
                        style={{
                            // Chiều rộng mỗi ảnh
                            width: `calc(${100 / visibleImages}% - ${(gap * (visibleImages - 1)) / visibleImages}px)`,
                        }}
                    >
                        <AnimeCard imgSrc={img} alt='img'  className="rounded-full"/>
                    </div>
                ))}
            </div>

            {/* Nút sang phải */}
            <button
                className="bg-red-700 bg-opacity-90 self-center h-fit absolute right-0 z-10"
                onClick={toRight}
            >
                &#8594;
            </button>
        </div>
    );
}
