import React, { useEffect, useState } from 'react'
import ListAnime from '../Component/ListAnime'
import ListBigAnimeCard from '../Component/ListBigAnimeCard'
import AnimeCard2 from '../Component/AnimeCard2'
import RandomAnime from '../Component/RandomAnime';
import { getAnime } from '../Service/Anime';

export default function HomePage() {
  const [AnimeList,SetAnimeList]=useState<Anime>([])

  const uri = [
    "https://cdn.animevietsub.biz/data/poster/2024/11/30/animevsub-ptJf6ZLFGE.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-TIHly52n6l.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/11/03/animevsub-DYEUQETfFh.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-Amu2bMVysF.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/07/28/animevsub-ch82vKB7lz.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/11/03/animevsub-DYEUQETfFh.jpg",
    "https://cdn.animevietsub.biz/data/poster/2024/10/13/animevsub-Amu2bMVysF.jpg",

  ];

  useEffect(()=>{
    getAnime().then((data)=>{
      SetAnimeList(data)
    })
  },[])

  return (
    <div className='justify-center flex h-full'>
      <div className='w-[60vw] p-5 flex flex-col bg-slate-950 pt-5 h-full'>
        <ListAnime />
        <div className='flex justify-between mt-5'>
          <div className='w-[70%]'>
            <ListBigAnimeCard />
            <div className='text-white text-[18px] p-2 bg-custom-gradient w-fit rounded-lg font-semibold mt-4'>
              DANH SÁCH ANIME
            </div>
            <div className='mt-4 grid grid-cols-5 gap-4'>
              {AnimeList.map((item) => (
                <AnimeCard2 imgSrc={item.Img} />
              ))}
            </div>
            <button className='w-full text-white bg-slate-900 mt-4 p-2 font-bold text-[18px] rounded hover:text-slate-700 duration-200'>XEM THÊM...</button>
          </div>
          <div className='w-[28%]'>
              <RandomAnime/>
          </div>
        </div>
      </div>
    </div>
  )
}
