import React, { useEffect } from "react";

const AnimeCommentSection = ({ url }) => {
  useEffect(() => {
    // Kiểm tra xem Facebook SDK đã được tải chưa
    if (window.FB) {
      window.FB.XFBML.parse(); // Kích hoạt lại Facebook plugin nếu cần
    }
  }, [url]); // Khi URL thay đổi, làm mới khung bình luận

  return (
    <div className="facebook-comments">
      {/* Facebook Comments Plugin */}
      <div
        className="fb-comments"
        data-href={url} // URL hiển thị comments
        data-width="100%" // Chiều rộng tự động
        data-numposts="5" // Hiển thị 5 bình luận
        data-order-by="reverse_time" // Sắp xếp bình luận từ mới nhất
      ></div>
    </div>
  );
};

export default AnimeCommentSection;
